import React  from "react";

import Send from './send'


//  import { Data } from "./data";

//  import * as XLSX from "xlsx";
 import * as xlsx from "xlsx";

class ExcelToJson extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      file: "",
      dataInFile:[]
    };
  }







   readUploadFile = (e) => {
    e.preventDefault();
    if (e.target.files) {
        const reader = new FileReader();
        reader.onload = (e) => {
            const data = e.target.result;
            const workbook = xlsx.read(data, { type: "array" });
            const sheetName = workbook.SheetNames[0];
            const worksheet = workbook.Sheets[sheetName];
            const json = xlsx.utils.sheet_to_json(worksheet);
            console.log(json);
            this.setState({
              dataInFile: json
            })
        };
        reader.readAsArrayBuffer(e.target.files[0]);

    }

  }



  render() {
    return (
      <div>


        <input
          type="file"
          id="file"
          ref="fileUploader"
          onChange={this.readUploadFile}

        />
        {/* {JSON.stringify(this.state.dataInFile[0] )} */}

        <br/>
        <br/>
        <br/>
        <br/>
        <table class="table table-striped" style={{borderBlockStyle:"solid",border: '1px solid black'}} >
                <thead >
                    <tr style={{border: '1px solid black'}}>

                    <th  style={{border: '1px solid black'}}>id</th>
                    <th  style={{border: '1px solid black'}}>Name</th>
                    <th  style={{border: '1px solid black'}}>age</th>
                    <th>  hyperlink</th>
                    {/* https://api.whatsapp.coms/send?phone */}
                    </tr>
                </thead>
                <tbody>

        {/* {this.state.dataInFile[1]} */}

                {this.state.dataInFile.map( info =>
                 (
                  <tr >
                  <td style={{border: '1px solid black'}}> {info.Id}</td>
                  <td style={{border: '1px solid black'}}>{info.name}</td>
                  <td style={{border: '1px solid black'}}>{info.age}</td>
                  {/* <td><a href="https://wa.me/7019616404?text={info.age}"/>{info.hyperlink}</td> */}
                  </tr>


                 )
                )}   
                    <Send/>          
                </tbody>
            </table>

      </div>
    );
  }
}

export default ExcelToJson;

